mapper.select:
SELECT a.*,
b.province, b.city,b.district,b.detailedAddress,
d.countAppointment
FROM student a left join address b on a.userId = b.userId
AND b.province = #{province}
AND b.city = #{city}
AND b.district = #{district}
left join (SELECT count(*) as countAppointment,studentId from appointment GROUP BY studentId) d on a.userId = d.studentId

    id = #{id}
    AND a.userId LIKE CONCAT('%', #{userId}, '%')
    AND a.name LIKE CONCAT('%', #{name}, '%')
    AND gender = #{gender}
    AND primarySchool = #{primarySchool}
    AND middleSchool = #{middleSchool}
    AND highSchool = #{highSchool}
    AND mobile = #{mobile}
    AND academicQualification = #{academicQualification}

ORDER BY d.countAppointment
LIMIT #{pageSize}
OFFSET #{offSet}
;

SELECT count(*)
FROM student a left join address b on a.userId = b.userId
AND b.province = #{province}
AND b.city = #{city}
AND b.district = #{district}

    id = #{id}
    AND a.userId LIKE CONCAT('%', #{userId}, '%')
    AND a.name LIKE CONCAT('%', #{name}, '%')
    AND gender = #{gender}
    AND primarySchool = #{primarySchool}
    AND middleSchool = #{middleSchool}
    AND highSchool = #{highSchool}
    AND mobile = #{mobile}
    AND academicQualification = #{academicQualification}
;

mapper.update:
UPDATE student
name = #{name}, vxName = #{vxName}, gender = #{gender}, primarySchool = #{primarySchool}, middleSchool = #{middleSchool}, highSchool = #{highSchool}, address = #{address}, mobile = #{mobile}, academicQualification = #{academicQualification}
WHERE userId = #{userId}
