mapper.select:
SELECT a.*,
b.province, b.city,b.district,b.detailedAddress,
d.countAppointment
FROM teacher a left join address b on a.userId = b.userId
AND b.province = #{province}
AND b.city = #{city}
AND b.district = #{district}
left join (SELECT count(*) as countAppointment,teacherId from appointment GROUP BY teacherId) d on a.userId = d.teacherId

    a.id = #{id}
    AND a.userId LIKE CONCAT('%', #{userId}, '%')
    AND a.name LIKE CONCAT('%', #{name}, '%')
    AND a.gender = #{gender}
    AND a.primarySchool = #{primarySchool}
    AND a.middleSchool = #{middleSchool}
    AND a.highSchool = #{highSchool}
    AND a.university = #{university}
    AND a.address = #{address}
    AND a.mobile = #{mobile}
    AND a.teachingSeniority = #{teachingSeniority}
    AND a.teachingType = #{teachingType}
    AND a.academicQualification = #{academicQualification}

ORDER BY d.countAppointment
LIMIT #{pageSize} OFFSET #{offSet};

SELECT count(*)
FROM teacher a left join address b on a.userId = b.userId
AND b.province = #{province}
AND b.city = #{city}
AND b.district = #{district}

    a.id = #{id}
    AND a.userId LIKE CONCAT('%', #{userId}, '%')
    AND a.name LIKE CONCAT('%', #{name}, '%')
    AND a.gender = #{gender}
    AND a.primarySchool = #{primarySchool}
    AND a.middleSchool = #{middleSchool}
    AND a.highSchool = #{highSchool}
    AND a.university = #{university}
    AND a.address = #{address}
    AND a.mobile = #{mobile}
    AND a.teachingSeniority = #{teachingSeniority}
    AND a.teachingType = #{teachingType}
    AND a.academicQualification = #{academicQualification}

mapper.update:
UPDATE teacher
name = #{name}, vxName = #{vxName}, gender = #{gender}, primarySchool = #{primarySchool}, middleSchool = #{middleSchool}, highSchool = #{highSchool}, university = #{university}, chinaIdNumber = #{chinaIdNumber}, personalizedSignature = #{personalizedSignature}, address = #{address}, mobile = #{mobile}, teachingSeniority = #{teachingSeniority}, teachingType = #{teachingType}, personalProfile = #{personalProfile}, academicQualification = #{academicQualification}, myStrengths = #{myStrengths}, price = #{price}, level = #{level}, lifePicture = #{lifePicture},
WHERE userId = #{userId}
